import { useState,useEffect } from "react"

const Activityy = ()=> {
    const [activity,setActivity] = useState([])

    useEffect(()=>{
        fetchData()
    },[])

    const fetchData = async ()=>{
        const res = await fetch('http://www.boredapi.com/api/activity/')
        setActivity(await res.json())
    }

    return(
        <section>
            <div className="card">
                <h1>{activity.activity}</h1>
                <h3>Type:{activity.type}</h3>
                <h4>Participants:{activity.participants}</h4>
                {/* {activity.accessibility<0.3 && <h3>Difficulty:Easy</h3>}
                {activity.accessibility>0.3<0.6 && <h3>Difficulty:Medium</h3>}
                {activity.accessibility<0.6 && <h3>Difficulty:Hard</h3>} */}
            </div>
        </section>
    );
}
export default Activityy;
