import { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import './style.css';

const Card = ()=>{
    const ed = "education";
    window.addEventListener('DOMContentLoaded',()=>{
        const sel = document.querySelector('select');
        sel.addEventListener('select',(e)=>{
        let val = e.target.value;
        window.location.href = "/activity?type="+val;
    })
    })

    return(
        <section>
            <div className="card">
                <span className="top">Ready to kill your<br />boredom 🍻</span>
                <span className="bottom">Get a task</span>
                <div className="controls">
                <Link to="/activity"><button className="btn">Random</button></Link>
                <div className="drop-down">
                    <button className="btn">Select Type</button>
                    <ul className='type-menu'>
                        <li><Link to={'/activity/education'} style={{textDecoration:"none"}}>Eduction</Link></li>
                        <li><Link to={'/activity/recreational'} style={{textDecoration:"none"}}>Recreational</Link></li>
                        <li><Link to={'/activity/Social'} style={{textDecoration:"none"}}>Social</Link></li>
                        <li><Link to={'/activity/diy'} style={{textDecoration:"none"}}>DIY</Link></li>
                        <li><Link to={'/activity/charity'} style={{textDecoration:"none"}}>Charity</Link></li>
                        <li><Link to={'/activity/cooking'} style={{textDecoration:"none"}}>Cooking</Link></li>
                        <li><Link to={'/activity/relaxation'} style={{textDecoration:"none"}}>Relaxation</Link></li>
                        <li><Link to={'/activity/music'} style={{textDecoration:"none"}}>Music</Link></li>
                        <li><Link to={'/activity/busywork'} style={{textDecoration:"none"}}>Busywork</Link></li>

                    </ul>
                </div>
                </div>
            </div>
        </section>
    );
}

export default Card;
