import { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import './style.css';

const Card = ()=>{

    window.addEventListener('DOMContentLoaded',()=>{
        const sel = document.querySelector('select');
        sel.addEventListener('select',(e)=>{
        let val = e.target.value;
        window.location.href = "/activity?type="+val;
    })
    })

    return(
        <section>
            <div className="card">
                <span className="top">Ready to kill your<br />boredom üçª</span>
                <span className="bottom">Get a task</span>
                <div className="controls">
                <Link to="/activity"><button className="btn">Random</button></Link>
                <span></span>
                <ul className='drop-down'>
                    <li><Link to="/activity?type=education">Eduction</Link> </li>
                    <li>Recreational</li>
                    <li>Social</li>
                    <li>DIY</li>
                    <li>Charity</li>
                    <li>Cooking</li>
                    <li>Relaxation</li>
                    <li>Music</li>
                    <li>Busywork</li>
                </ul>
                </div>
            </div>
        </section>
    );
}

export default Card;
